    private void s_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_s_btnActionPerformed
        String cid = cid_txt.getText();
        String name = cn_txt.getText();

        if("".equals(cid) || cid.isEmpty()){
            JOptionPane.showMessageDialog(rootPane,"Enter Category Name.");
        }else if("".equals(name) || name.isEmpty()){
            JOptionPane.showMessageDialog(rootPane,"Enter category name.");
        }else{
            try {
                db.putdata("INSERT INTO category VALUES('"+cid+"','"+name+"')");
                clear();
                tableDataClear();
                tableDataLoader();

            }catch (Exception e){
                System.out.println("Error :"+e);
            }
        }
    }//GEN-LAST:event_s_btnActionPerformed

    private void d_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_d_btnActionPerformed
        if(!cid_txt.getText().isEmpty()){
            int cid = Integer.parseInt(cid_txt.getText());
            try {
                db.putdata("DELETE FROM category WHERE cat_id = '"+cid+"'");
                tableDataClear();
                tableDataLoader();
                clear();

            } catch (Exception e) {
                System.out.println("Delete: "+e);
            }
        }
    }//GEN-LAST:event_d_btnActionPerformed

    private void ctableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ctableMouseClicked
        int x =  ctable.getSelectedRow();
        DefaultTableModel table = (DefaultTableModel) ctable.getModel();

        cid_txt.setText(table.getValueAt(x,0).toString());
        cn_txt.setText(table.getValueAt(x,1).toString());

        s_btn.setEnabled(false);
        cid_txt.setEditable(false);
        u_btn.setEnabled(true);

    }//GEN-LAST:event_ctableMouseClicked

