      //text field clear
    private void  clear(){
        cid_txt.setText("");
        cn_txt.setText("");
        
        
    }
    
     // Supplier jTable Data Clear
    private void tableDataClear(){
        try {
            while (0 < ctable.getRowCount()) {                
                DefaultTableModel table = (DefaultTableModel) ctable.getModel();
                table.removeRow(ctable.getRowCount()-1);
            }
        } catch (Exception e) {
           System.out.println("TableDataClear :"+e); 
        } 
    }
    
     // Supplier Data load to Jtable
    private void tableDataLoader(){
        try {
            ResultSet result = db.getdata("SELECT * FROM category");
            DefaultTableModel table = (DefaultTableModel) ctable.getModel();
            
            int x  = 0;
            while(result.next()){
                Vector v = new Vector();
                v.add(null);
                table.addRow(v);
                
                String cid = result.getString("cat_id");
                String name = result.getString("name");
               
                table.setValueAt(cid, x, 0);
                table.setValueAt(name, x, 1);
                
                
                x++;
            }
            
        } catch (Exception e) {
            System.out.println("TableDataLoader :"+e);
        }
    }
    
    
    
