    private void s_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_s_btnActionPerformed
        String Iid = Iid_txt.getText();
        String name = n_txt.getText();
        String uprice = u_txt.getText();

        if("".equals(Iid) || Iid.isEmpty()){
            JOptionPane.showMessageDialog(rootPane,"Enter Item Id.");
        }else if("".equals(name) || name.isEmpty()){
            JOptionPane.showMessageDialog(rootPane,"Enter Item name.");
        }else if("".equals(uprice) || uprice.isEmpty()){
            JOptionPane.showMessageDialog(rootPane,"Enter Unit price.");
        }else{
            try {
                db.putdata("INSERT INTO item VALUES('"+Iid+"','"+name+"','"+uprice+"')");
                clear();

                tableDataClear();

            }catch (Exception e){
                System.out.println("Error1 :"+e);

            }
        }

    }//GEN-LAST:event_s_btnActionPerformed

