    //text field clear
    private void  clear(){
        Iid_txt.setText("");
        n_txt.setText("");
        u_txt.setText("");
        
    }
    
    // Supplier jTable Data Clear
    private void tableDataClear(){
        try {
            while (0 < itemtable.getRowCount()) {                
                DefaultTableModel table = (DefaultTableModel) itemtable.getModel();
                table.removeRow(itemtable.getRowCount()-1);
            }
        } catch (Exception e) {
           System.out.println("TableDataClear :"+e); 
        } 
    }
    
     // Supplier Data load to Jtable
    private void tableDataLoader(){
        try {
            ResultSet result = db.getdata("SELECT item_id,name,unit_price FROM item");
            DefaultTableModel table = (DefaultTableModel) itemtable.getModel();
            
            int x  = 0;
            while(result.next()){
                Vector v = new Vector();
                v.add(null);
                table.addRow(v);
                
                String Iid = result.getString("item_id");
                String name = result.getString("name");
                String uprice = result.getString("unit_price");
                
                
                table.setValueAt(Iid, x, 0);
                table.setValueAt(name, x, 1);
                table.setValueAt(uprice, x, 2);
                
                
                x++;
            }
            
        } catch (Exception e) {
            System.out.println("TableDataLoader :"+e);
        }
    }
    
    
